"use strict";
module.exports = (options) => {
    const token = options.token ? options.token : "your_token";
    const file = options.configFileType;
    function typeConfig() {
        if (file === "json")
            return `{
  "DISCORD_TOKEN": "${token}"
}
`;
        else
            return `export = {
  DISCORD_TOKEN: "${token}",
};
`;
    }
    return [typeConfig(), `config.${file}`];
};
